import styled from 'styled-components';
import {
  FiMic,
  FiRadio,
  FiMusic,
  FiUser,
  FiBookOpen,
  FiFolder,
} from 'react-icons/fi';
import Link from 'next/link';

const primary = '#309F5E';
const TopSideBar = () => (
  <Wrapper>
    <Heading>Huda</Heading>
    <List>
      <FiMic color={primary} />
      <Link href="/podcast">
        <LinkItem>Podcast</LinkItem>
      </Link>
    </List>
    <List>
      <FiMusic color={primary} />
      <Link href="/">
        <LinkItem>Explore</LinkItem>
      </Link>
    </List>
    <List>
      <FiUser color={primary} />
      <Link href="/sheikh">
        <LinkItem>Sheikh</LinkItem>
      </Link>
    </List>
    <List>
      <FiBookOpen color={primary} />
      <Link href="/topics">
        <LinkItem>Books</LinkItem>
      </Link>
    </List>
    <List>
      <FiFolder color={primary} />
      <Link href="/books">
        <LinkItem>Topics</LinkItem>
      </Link>
    </List>
  </Wrapper>
);

const Wrapper = styled.aside`
  /* background-color:#F6F7FB; */
  background-color: white;
  padding-top: 23px;
  padding-left: 25px;
  width: 165px;
  height: 100vh;
  border-right: solid 1px #f0f0f0;
`;
const Heading = styled.h1`
  font-size: 32px;
  margin-bottom: 43px;
  color: var(--green);
`;

const List = styled.li`
  list-style: none;
  display: flex;
  margin-bottom: 24px;
  gap: 10px;
`;
const LinkItem = styled.a`
  text-decoration: none;
  color: hsl(0, 0%, 49%);
  font-size: 14px;
`;
export default TopSideBar;
