/* eslint-disable arrow-body-style */
/* eslint-disable prettier/prettier */
import styled from 'styled-components';
import {FiMic,FiRadio } from 'react-icons/fi';
import Link from 'next/link'

const primary ='#309F5E';
const Sidebar = () => {
    return (
        <Wrapper>
            <Heading>Huda</Heading>
            <List>
                <FiMic color={primary}/>
                <Link href="/podcast"><LinkItem >Podcast</LinkItem></Link>
            </List>
            <List>
                <FiRadio color={primary}/>
                <Link href="/radio"><LinkItem >Radio</LinkItem></Link>
            </List>
        </Wrapper>
    )
}
const Wrapper = styled.aside`
/* background-color:#F6F7FB; */
background-color:white;
padding-top:23px;
padding-left:25px;
width:165px;
height:100vh;
border-right:solid 1px #F0F0F0;
`
const Heading = styled.h1`
font-size:32px;
margin-bottom:43px;
color:var(--green);
`

const List = styled.li`
list-style:none;
display:flex;
margin-bottom:24px;
gap:10px;

`;
const LinkItem = styled.a`
text-decoration:none;
color:#a9a9a9;
font-size:14px;

`
export default Sidebar;
